import React, { useState } from "react";
import "./navbar.css";
import { onAuthStateChanged } from "firebase/auth";
import { auth } from "../../utils/config";
import logo from "../../assets/logosvg.svg";

function Navbar() {
  const [userEmail, setUserEmail] = useState({});
  const [isVisible, setIsVisible] = useState(true);
  onAuthStateChanged(auth, (currentUser) => {
    setUserEmail(currentUser);
  });

  const openRegister = () => {
    document.getElementById("regcontainer").style.top = "0%";
  };
  const openLogin = () => {
    document.getElementById("logcontainer").style.top = "0%";
  };

  const hideLogNav = () => {
    document.getElementById("userStatebtn").style.display = "none";
    document.getElementById("userStatebtn2").style.display = "none";
  };
  const showLogNav = () => {
    document.getElementById("userStatebtn").style.display = "none";
    document.getElementById("userStatebtn2").style.display = "none";
  };

  if (userEmail.email) {
    hideLogNav();
  } else {
    showLogNav();
  }

  return (
    <div className="nav">
      <div className="logo">
        <img src={logo} alt="" srcset="" />
      </div>
      <div className="profile">
        <div className="login" id="userStatebtn" onClick={openLogin}>
          Login
        </div>
        <div className="login reg" id="userStatebtn" onClick={openRegister}>
          Sign Up
        </div>
        <div className="profile-icon">
          <div className="name">{userEmail?.email}</div>
          <div className="icon">
            {userEmail?.email?.charAt(0).toUpperCase()}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Navbar;
