import React, { useEffect, useContext } from "react";
import { db } from "../utils/config";
import {
  collection,
  setDoc,
  updateDoc,
  doc,
  arrayUnion,
  arrayRemove,
} from "firebase/firestore";
import { DataContext } from "./DataContext";

function SendTestData() {
  const { userData, userAuthState, siteDate, tradeEntryCount } =
    useContext(DataContext);

  useEffect(() => {
    const testDataSend = async () => {
      let s1 = "Straddle";
      let s2 = "Stragle";
      let entryCount = tradeEntryCount;
      let data = {
        entryCount: 0,
        [entryCount]: {
          date: siteDate.c.day + "-" + siteDate.c.month + "-" + siteDate.c.year,
          noOfStrats: 3,
          [s1]: {
            pnl: 6000,
            detail: {
              noOfTrade: 3,
              trade1: {
                scripName: "Nifty",
                direction: "Long",
                pnl: 2000,
              },
              trade2: {
                scripName: "Nifty",
                direction: "Long",
                pnl: 2000,
              },
              trade3: {
                scripName: "Nifty",
                direction: "Long",
                pnl: 2000,
              },
            },
          },
          [s2]: {
            pnl: 6000,
            detail: {
              noOfTrade: 3,
              trade1: {
                scripName: "Nifty",
                direction: "Long",
                pnl: 2000,
              },
              trade2: {
                scripName: "Nifty",
                direction: "Long",
                pnl: 2000,
              },
              trade3: {
                scripName: "Nifty",
                direction: "Long",
                pnl: 2000,
              },
            },
          },
        },
      };

      console.log(data);
      // for (let i = 0; i < 5; i++) {
      //   data.push(data[i]);
      // }

      const testingSendingData = doc(db, "users", "devscryptcomp@gmail.com");
      // await updateDoc(testingSendingData, { strategyTags: arrayUnion(data) });
      await setDoc(testingSendingData, { tradesData: data }, { merge: true });
    };
    testDataSend();
  }, [tradeEntryCount]);
  return <div></div>;
}

export default SendTestData;
