  useEffect(() => {
    const getTradeData = async () => {
      for (var i = 0; i < userData?.tradeDataDocList?.length; i++) {
        onSnapshot(
          doc(db, "usersTradeData", userData?.tradeDataDocList[i]),
          (doc) => {
            userDataMap.set(doc.data());
            console.log(userDataMap);
          }
        );
      }
    };
    getTradeData();
  }, [userData]);

  return (
    <div>
      <DataContext.Provider value={{ userData, userAuthState, siteDate }}>
        {/* <AddStrategy /> */}
        <Profile />
        <Register />
        <Login />
        <Navbar />
        <TopBar />
        <SendTestData />
      </DataContext.Provider>
    </div>
  );
}

export default App;
