import React, { useState } from "react";
import "./navbar.css";
import { onAuthStateChanged } from "firebase/auth";
import { auth } from "../../utils/config";
import logo from "../../assets/logosvg.svg";

function Navbar() {
  const [userEmail, setUserEmail] = useState({});
  onAuthStateChanged(auth, (currentUser) => {
    setUserEmail(currentUser);
    if (currentUser) {
      document.getElementsByClassName("login").style.display = "none";

      document.getElementsByClassName("name").innerHTML = userEmail.email;
    }
  });

  const openRegister = () => {
    document.getElementById("regcontainer").style.top = "0%";
  };
  const openLogin = () => {
    document.getElementById("logcontainer").style.top = "0%";
  };

  return (
    <div className="nav">
      <div className="logo">
        <img src={logo} alt="" srcset="" />
      </div>
      <div className="profile">
        <div className="login" onClick={openLogin}>
          Login{" "}
        </div>
        <div className="login reg" onClick={openRegister}>
          Sign Up
        </div>
        <div className="profile-icon">
          <div className="name"></div>
          <div className="icon">F</div>
        </div>
      </div>
    </div>
  );
}

export default Navbar;
