import React, { useState, useEffect, useContext } from "react";
import { db } from "../utils/config";
import { getDoc, setDoc, updateDoc, doc } from "firebase/firestore";
import { onAuthStateChanged } from "firebase/auth";
import { auth } from "../utils/config";

import { DataContext } from "./DataContext";

function CheckDocSize() {
  const { userData, userAuthState, siteDate, userTradeData } =
    useContext(DataContext);
  const [fetchedData, setFetchedData] = useState();

  onAuthStateChanged(auth, (currentUser) => {
    if (currentUser?.email) {
      checkAndAdd();
    }
  });

  const checkAndAdd = async () => {
    const docData = await getDoc(
      db,
      "users",
      userAuthState?.email,
      "userTradeData",
      userData?.tradeWritableDoc
    );

    if (docData.exists) {
      setFetchedData(docData.data());
    } else {
      console.log("no doc");
    }
  };

  useEffect(() => {
    console.log("hell", fetchedData);
  }, [fetchedData]);

  return <div></div>;
}

export default CheckDocSize;
