import React, { useEffect, useState } from "react";
import { db } from "./utils/config";
import {
  doc,
  onSnapshot,
  collection,
  getDoc,
  setDoc,
  updateDoc,
} from "firebase/firestore";
import { DataContext } from "./service/DataContext";
import { onAuthStateChanged } from "firebase/auth";
import { auth } from "./utils/config";
import { DateTime } from "luxon";

import "./style/app.css";
import Navbar from "./components/navbarcomp/Navbar";
import Register from "./components/registercomp/Register";
import Login from "./components/logincomp/Login";
import Profile from "./components/profilecomp/Profile";
import SendTestData from "./service/SendTestData";
import TopBar from "./components/topbarcomp/TopBar";
import AddStrategy from "./components/addstrategycomp/AddStrategy";
// import CheckDocSize from "./service/CheckDocSize";

function App() {
  var siteDate = DateTime.now().setZone("local");

  const [userData, setUserData] = useState();
  const [allData, setAllData] = useState({});
  const [userAuthState, setUserAuthState] = useState("");

  onAuthStateChanged(auth, (currentUser) => {
    setUserAuthState(currentUser);
  });

  useEffect(() => {
    const checkAndAdd = async () => {
      try {
        const docDataFetch = await getDoc(
          doc(
            db,
            "users",
            userAuthState?.email,
            "userTradeData",
            userData?.tradeWritableDoc
          )
        );
        if (docDataFetch?.exists()) {
          setFetchedData(docDataFetch?.data());
          console.log("nullllll", fetchedData);
        } else {
          console.log("no doc");
        }
      } catch (err) {
        console.log(err);
      }
    };
  }, [userData]);

  useEffect(() => {
    const getDataSnap = async () => {
      if (userAuthState) {
        const unsubscribe = onSnapshot(
          doc(db, "users", userAuthState?.email),
          (doc) => {
            console.log("Current data : ", doc.data());
            setUserData(doc.data());
          }
        );
      }
    };
    getDataSnap();
  }, [userAuthState]);

  useEffect(() => {
    if (userAuthState) {
      const unsub = onSnapshot(
        collection(db, "users", userAuthState?.email, "userTradeData"),
        (querySnapshot) => {
          const newData = {};
          querySnapshot.forEach((doc) => {
            const documentData = doc.data();
            const documentId = doc.id;
            newData[documentId] = documentData;
          });
          setAllData(newData);
        }
      );
    }
  }, [userData]);

  useEffect(() => {
    console.log(allData);
  }, [allData]);

  return (
    <div>
      <DataContext.Provider value={{ userData, userAuthState, siteDate }}>
        {/* <AddStrategy /> */}
        <Profile />
        <Register />
        <Login />
        <Navbar />
        <TopBar />
        {/* <SendTestData /> */}
        {/* <CheckDocSize /> */}
      </DataContext.Provider>
    </div>
  );
}

export default App;
