import React, { useState } from "react";
import "./navbar.css";
import { onAuthStateChanged } from "firebase/auth";
import { auth } from "../../utils/config";
import logo from "../../assets/logosvg.svg";

function Navbar() {
  const [userEmail, setUserEmail] = useState({});
  onAuthStateChanged(auth, (currentUser) => {
    setUserEmail(currentUser);
  });

  var loginButton = document.getElementsByClassName("login");

  if (userEmail.email) {
    loginButton.forEach((a) => (a.style.display = "none"));
  } else {
    loginButton.forEach((a) => (a.style.display = "none"));
  }

  const openRegister = () => {
    document.getElementById("regcontainer").style.top = "0%";
  };
  const openLogin = () => {
    document.getElementById("logcontainer").style.top = "0%";
  };

  return (
    <div className="nav">
      <div className="logo">
        <img src={logo} alt="" srcset="" />
      </div>
      <div className="profile">
        <div className="login" id="userStatebtn" onClick={openLogin}>
          Login
        </div>
        <div className="login reg" id="userStatebtn2" onClick={openRegister}>
          Sign Up
        </div>
        <div className="profile-icon">
          <div className="name">{userEmail?.email}</div>
          <div className="icon">
            {userEmail?.email?.charAt(0).toUpperCase()}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Navbar;
