import React, { useEffect, useState } from "react";
import { db } from "./utils/config";
import { doc, onSnapshot } from "firebase/firestore";
import { DataContext } from "./service/DataContext";
import { onAuthStateChanged, signOut } from "firebase/auth";
import { auth } from "./utils/config";
import { DateTime } from "luxon";

import "./style/app.css";
import Navbar from "./components/navbarcomp/Navbar";
import Register from "./components/registercomp/Register";
import Login from "./components/logincomp/Login";
import Profile from "./components/profilecomp/Profile";
import SendTestData from "./service/SendTestData";
import TopBar from "./components/topbarcomp/TopBar";
import AddStrategy from "./components/addstrategycomp/AddStrategy";

function App() {
  var siteDate = DateTime.now().setZone("local");

  const [userData, setUserData] = useState();
  const tempTradeData = [];
  const [userTradeData, setUserTradeData] = useState();
  const [userAuthState, setUserAuthState] = useState("");

  onAuthStateChanged(auth, (currentUser) => {
    setUserAuthState(currentUser);
  });

  useEffect(() => {
    const getDataSnap = async () => {
      if (userAuthState) {
        const unsubscribe = onSnapshot(
          doc(db, "users", userAuthState?.email),
          (doc) => {
            console.log("Current data: ", doc.data());
            setUserData(doc.data());
          }
        );
      }
    };
    getDataSnap();
  }, [userAuthState]);

  useEffect(() => {
    const collectionRef = firestore.collection("yourCollection");

    // Use onSnapshot to listen for real-time updates
    const unsubscribe = collectionRef.onSnapshot((snapshot) => {
      const dataMap = new Map();

      snapshot.forEach((doc) => {
        const userId = doc.id;
        const userData = doc.data();

        dataMap.set(userId, userData);
      });

      setUserData(dataMap);
    });

    // Cleanup function to unsubscribe from the snapshot listener when the component unmounts
    return () => unsubscribe();
  }, []);

  // var combinedObject;
  // useEffect(() => {
  //   const getTradeData = async () => {
  //     for (var i = 0; i < userData?.tradeDataDocList?.length; i++) {
  //       onSnapshot(
  //         doc(db, "usersTradeData", userData?.tradeDataDocList[i]),
  //         (doc) => {
  //           tempTradeData.push(doc.data());
  //           console.log(tempTradeData);
  //         }
  //       );
  //     }
  //   };
  //   getTradeData();
  // }, [userData]);

  return (
    <div>
      <DataContext.Provider
        value={{ userData, userAuthState, siteDate, tempTradeData }}
      >
        {/* <AddStrategy /> */}
        <Profile />
        <Register />
        <Login />
        <Navbar />
        <TopBar />
        <SendTestData />
      </DataContext.Provider>
    </div>
  );
}

export default App;
