import React, { useState } from "react";
import "./profile.css";
import { onAuthStateChanged, signOut } from "firebase/auth";
import { auth } from "../../utils/config";

function Profile() {
  const [userEmailProfile, setUserEmailProfile] = useState({});
  const [visible, setVisible] = useState(true);
  onAuthStateChanged(auth, (currentUser) => {
    setUserEmailProfile(currentUser);

    if (userEmailProfile?.email) {
      setVisible(false);
    } else {
      setVisible(true);
    }
  });

  const closeProfile = () => {
    document.getElementById("profileContainer").style.top = "100%";
  };
  const signOutUser = async () => {
    await signOut(auth);
  };
  return (
    <div
      className="profileInvisibleContainer"
      onClick={closeProfile}
      id="profileContainer"
    >
      <div className="profileContainer">
        <div className="exit" onClick={closeProfile}>
          <div className="line1"></div>
          <div className="line2"></div>
        </div>
        <div className="profileIconLetter">A</div>
        <div className="profileEmail">userEmailProfile</div>
        <div className="subscriptionData">
          <div className="type">Plan: Free</div>
          <div className="expiration">28 days left</div>
        </div>
        <div className="signOutBtn" onClick={signOutUser}>
          Sign Out
        </div>
      </div>
    </div>
  );
}

export default Profile;
